	var pen = { 		// the pen tool
		start: false,
		weight: 1,
		px: 0,
		py: 0,
		distanceBtw: function(p1x, p1y, p2x, p2y){
			return Math.sqrt(Math.pow(p2x - p1x, 2) + Math.pow(p2y - p1y, 2));
		},
		angleBtw: function(p1x, p1y, p2x, p2y){
			 return Math.atan2( p2x - p1x, p2y - p1y );
		},
		open: function(x,y){
			ctx.lineJoin = "round";
			ctx.lineCap = "round";
			ctx.beginPath();
			ctx.moveTo(x,y);
			this.px = x;  this.py = y;
		},
		connect: function(x,y){
			if(set.pen=='solid'){
				var dx = (this.px>x) ? this.px-x : x-this.px;
		    	var dy = (this.py>y) ? this.py-y : y-this.py;
		    	this.weight = Math.abs(dx-dy);
		    	if( this.weight > 100){ this.weight = 100 }
				ctx.lineWidth = Math.map(this.weight, 0, 100, set.h/2.5, set.h*2.5);

				ctx.lineTo(x,y);
				ctx.strokeStyle = "rgb("+set.r()+", "+set.g()+", "+set.b()+")";
				ctx.stroke();
				ctx.closePath();
				ctx.beginPath();
				ctx.moveTo(x,y);
				this.px = x;  this.py = y;
			}
			if(set.pen=='soft'){
				var dist = this.distanceBtw( this.px,this.py, x,y );
				var angle = this.angleBtw( this.px,this.py, x,y );
				for (var i = 0; i < dist; i++) {
					X = this.px + (Math.sin(angle)*i);
					Y = this.py + (Math.cos(angle)*i);
					var grd = ctx.createRadialGradient( X,Y, set.h/6, X,Y, set.h/2 );
					grd.addColorStop(0, 'rgba('+set.r()+", "+set.g()+", "+set.b()+',0.1)');
					grd.addColorStop(1, 'rgba('+set.r()+", "+set.g()+", "+set.b()+',0)');
					ctx.fillStyle = grd;
					ctx.fillRect(X-set.h/2, Y-set.h/2, set.h, set.h);
				};
				this.px = x;
				this.py = y;
			}
			if(set.pen=='lines' || set.pen=='calig'){
				ctx.strokeStyle = "rgb("+set.r()+", "+set.g()+", "+set.b()+")";
				if(set.pen=='lines'){ ctx.lineWidth = (set.h<1) ? 1 : set.h*0.05; }
				if(set.pen=='calig'){ ctx.lineWidth = set.h*0.2; }
				ctx.moveTo(this.px, this.py);
				ctx.lineTo(x, y);
				ctx.stroke();
				ctx.moveTo(this.px - set.h*0.2, this.py - set.h*0.2);
				ctx.lineTo(x - set.h*0.2, y - set.h*0.2);
				ctx.stroke();
				ctx.moveTo(this.px - set.h*0.1, this.py - set.h*0.1);
				ctx.lineTo(x - set.h*0.1, y - set.h*0.1);
				ctx.stroke();
				ctx.moveTo(this.px + set.h*0.1, this.py + set.h*0.1);
				ctx.lineTo(x + set.h*0.1, y + set.h*0.1);
				ctx.stroke();
				ctx.moveTo(this.px + set.h*0.2, this.py + set.h*0.2);
				ctx.lineTo(x + set.h*0.2, y + set.h*0.2);
				ctx.stroke();
				this.px = x; this.py = y;
			}
		},
		draw: function(mx,my){
			if(!this.start){ 
    			this.open(mx,my); 
    			this.start=true;  
    		} else { 
    			this.connect( mx,my );
    		}
		},
		reset: function(){
			this.start=false;
		}

	}




	var eraser = { 		// the eraser tool
		px: 0,
		py: 0,
		start: false,
		distanceBtw: function(p1x, p1y, p2x, p2y){
			return Math.sqrt(Math.pow(p2x - p1x, 2) + Math.pow(p2y - p1y, 2));
		},
		angleBtw: function(p1x, p1y, p2x, p2y){
			 return Math.atan2( p2x - p1x, p2y - p1y );
		},
		open: function(x,y){
			this.px = x; this.py = y;
		},
		erase: function(x,y){
			var dist = this.distanceBtw( this.px,this.py, x,y );
			var angle = this.angleBtw( this.px,this.py, x,y );
			// var size = Math.map(set.h, 0, 100, 0, 300);
			var size = set.h;
			for (var i = 0; i < dist; i++) {
				X = this.px + (Math.sin(angle)*i);
				Y = this.py + (Math.cos(angle)*i);
				var grd = ctx.createRadialGradient( X,Y, size/6, X,Y, size/2 );
				grd.addColorStop(0, 'rgba(255,255,255,0.1)');
				// grd.addColorStop(0.5, 'rgba(255,255,255,0.03)');
				grd.addColorStop(1, 'rgba(255,255,255,0)');
				ctx.fillStyle = grd;
				ctx.fillRect(X-size/2, Y-size/2, size, size);
			};
			this.px = x;
			this.py = y;
		}, 
		draw: function(mx,my){
			if(!this.start){
    			this.open(mx,my);
    			this.start = true;
    		} else {
    			this.erase(mx,my);
    		}
		},
		reset: function(){
			this.start = false;
		}
	}



