	// --------------- spray tool

	function drip(x,y,s,c,d){
		y += Date.now()*0.05-d;
		var a = Math.map(set.a, 0, 1, 0.25, 1);
		ctx.beginPath();
		ctx.fillStyle = c;
		ctx.arc(x,y,s,0,Math.PI*2,true); 
		ctx.fill(); ctx.closePath();
	}
	var spray = { 	
		paint: function(x,y){
			var r = Math.map(set.h, 0, 200, 1, 80); 
			var rx = (Math.random()*r/2) * Math.cos(Math.random()*Math.PI*2)
			var ry = (Math.random()*r/2) * Math.sin(Math.random()*Math.PI*2);
			var add = (r>10) ? r/10 : 1;
			var mult = (r>35) ? 10 : 5;
			var size = (Math.random()*mult) + add;
			var a = Math.map(set.a, 0, 1, 0.25, 1);
			ctx.beginPath();
			ctx.fillStyle = "rgba("+set.r()+", "+set.g()+", "+set.b()+","+a+")";	
			ctx.arc(x+rx,y+ry,size,0,Math.PI*2,true); // main larger splotch
			ctx.fill(); ctx.closePath();
			for (var i = 0; i < 5; i++) { // sprinkle
				var subSize = (size*0.2>=1) ? Math.random()*size-size*0.2 : Math.random()*size;
				ctx.arc(x+((Math.random()*r)-r/2),y+((Math.random()*r)-r/2),Math.abs(subSize),0,Math.PI*2,true);
				ctx.fill(); ctx.closePath();
			};
			var now = Date.now()*0.05;
			var sz = Math.random()*3;
			var time = (Math.random()*3)+8;
			var curCol = ctx.fillStyle;
			for (var i = 0; i < Math.round(r*0.8); i++) {	// drip on larger splotch
				setTimeout(function(){drip(x+rx,y+ry,sz,curCol, now)}, time*i);
			};
			
		},
		stars: function(x,y){
			ctx.lineJoin = "miter";
			ctx.lineCap = "butt";
			var size = Math.map(set.h,0,200,1,20);
			var angle = Math.random()*360;
			var scale = Math.random()*2;
			ctx.save();
			ctx.translate(x,y);
			ctx.beginPath();
			ctx.rotate(Math.PI / 180 * angle);
			ctx.scale(scale, scale);
			ctx.strokeStyle = "rgba("+set.r()+", "+set.g()+", "+set.b()+","+set.a+")";
			ctx.lineWidth = Math.random()*size;
			for (var i = 5; i--;) {
				ctx.lineTo(0, size);
				ctx.translate(0, size);
				ctx.rotate((Math.PI * 2 / 10));
				ctx.lineTo(0, -size);
				ctx.translate(0, -size);
				ctx.rotate(-(Math.PI * 6 / 10));
			}
			ctx.lineTo(0, size);
			ctx.closePath();
			ctx.stroke();
			ctx.restore();
		},
		leafs: function(x,y){
			var angle = Math.random() * 360;
			var scale = Math.random()*1+1;
			var size = Math.map(set.h,0,200,5,100);
			var a = Math.random() * size/2 - size/4;
			var b = Math.random() * size/2 - size/4;
			var c = Math.random() * size/2 - size/4;
			var d = Math.random() * size/2 - size/4;

			ctx.save();
			ctx.translate(x, y);
			ctx.beginPath();
			ctx.rotate(Math.PI / 180 * angle);
			ctx.scale(scale, scale);
			ctx.fillStyle = "rgba("+set.r()+", "+set.g()+", "+set.b()+","+set.a+")";
			ctx.beginPath();
			ctx.moveTo(0, 0);
			ctx.bezierCurveTo(a+size, b+0, c+0, d-size, 0, 0);
			ctx.closePath();
			ctx.fill();
			ctx.restore();
		},
		webpoints: {x:[],y:[]},
		distance: function(x,y,cx,cy) {
  			return Math.sqrt(Math.pow(cx - x, 2) + Math.pow(cy - y, 2));
		},
		web: function(x,y){
			var radius = set.h;
			var rx = (Math.random()*radius/2) * Math.cos(Math.random()*Math.PI*2)
			var ry = (Math.random()*radius/2) * Math.sin(Math.random()*Math.PI*2);
			var px = x+rx;
			var py = y+ry;
			this.webpoints.x.push(px);
			this.webpoints.y.push(py);
			ctx.fillStyle = "rgb("+set.r()+", "+set.g()+", "+set.b()+")";	
			ctx.strokeStyle = "rgba("+set.r()+", "+set.g()+", "+set.b()+","+set.a+")";
			ctx.beginPath();
			ctx.arc(px,py,1,0,Math.PI*2,true);
			ctx.closePath();
			ctx.fill();
			for (var i = 0; i < this.webpoints.x.length; i++) {
				var ix = this.webpoints.x[i];
				var iy = this.webpoints.y[i];
				if(this.distance(px,py,ix,iy)<50){
					ctx.lineWidth = 1;
					ctx.beginPath();
					ctx.moveTo(px,py);
					ctx.lineTo(ix,iy);
					ctx.closePath();
					ctx.stroke();
				}
			};
		},
		draw: function(mx, my ){
			if(set.spray=='paint'){ spray.paint(mx,my); }
    		if(set.spray=='stars'){ spray.stars(mx,my);}
    		if(set.spray=='leafs'){ spray.leafs(mx,my);}
    		if(set.spray=='web'){ spray.web(mx,my);}
		}

	}
	